function [ found ] = checkClass( classification, classes )
%CHECKCLASS Check is the classification contains some specified classes
    %
    % Input
    % classification : cell{class,score} (generated by the classifier)
    % classes : classes to look for cell{class,score threshold}
    %
    % Output
    % res : boolean
    %
    
    found = 0;
    for i=1:size(classes,1)
        lookClass = classes{i,1};   % Current searched class and score
        lookScore = classes{i,2};
        for j=1:size(classification,1)
            score = classification{j,2};
            if score >= lookScore   % Check if the current score is larger than current one
                class = classification{j,1};
                if ~isempty(strfind(class,lookClass))   % Does current class contain the reached one?
                    found = 1;
                    return
                end
            end            
        end        
    end
end

